    if 'z_value' not in result.colnames:
        # No redshift info, skip filtering
        return result
    mask = []
    for z in result['z_value']:
        try:
            if z is not None and abs(z - z_lens) <= delta_z:
                mask.append(True)
            else:
                mask.append(False)
        except Exception:
            mask.append(False)
    return result[mask]

